import {useState} from 'react'

export function LoadPrice() {

    var [item, setItem] = useState('items')
    console.log(item);

    displayPrice = () => {
        var price = "-"
        if (item === 'item1') {
            price = "$1"
        } else if (item === 'item3'){
            price = "$3"
        }
        return price
    }

    return(
        <><input
            value={item}
            onChange={e => setItem(e.target.value)}
        >
        </input>
        <div>
            Price is {displayPrice}
        </div></>

    )
};
